import { Component, TemplateRef } from '@angular/core';
import { BsModalService, BsModalRef } from 'ngx-bootstrap/modal';

interface produto {
  nome:string, preço: string, categoria:string, descrição: string
}

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.scss']
})
export class HomeComponent {

  public produtos: produto[] = [
    {
      nome: 'Sanduiche gaiteiro',
      preço: '14,99',
      categoria: 'Sanduíche',
      descrição: 'bão dms'
    },
    {
      nome: 'Beirute',
      preço: '14,99',
      categoria: 'Sanduíche',
      descrição: 'bão dms'
    },
    {
      nome: 'Pizza',
      preço: '14,99',
      categoria: 'Pizza',
      descrição: 'bão dms'
    },
    {
      nome: 'Coca-cola',
      preço: '14,99',
      categoria: 'Bebidas',
      descrição: 'bão dms'
    },
    {
      nome: 'Grogue-Express',
      preço: '14,99',
      categoria: 'drinks',
      descrição: 'bão dms'
    }
  ]

  public categorias: string[] = []

  modalRef?: BsModalRef;
  constructor(private modalService: BsModalService) {
    this.organizarCategorias();
  }

  openModal(template: TemplateRef<any>) {
    this.modalRef = this.modalService.show(template);
  }

  organizarCategorias() {
    for (let produto of this.produtos){
      const findCategory: string | undefined = this.categorias.find(categoria => produto.categoria === categoria)
        if(!findCategory){
          this.categorias.push(produto.categoria)
        }
    }
  }
}
<header>
  <img src="../../assets/burger-fast-food-svgrepo-com.svg" alt="" />
  <h1>E-Menu</h1>
</header>
<main>
  <section>
    <accordion *ngFor="let categoria of categorias" [isAnimated]="true">
      <accordion-group [heading]="categoria">
        <div *ngFor="let produto of produtos" class="categories-organizer">
          <div ngIf="produto.categoria === categoria">/
            <p>{{ produto.nome }}</p>
          </div>
        </div>
      </accordion-group>
    </accordion>
  </section>

