import { Component } from '@angular/core';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.scss']
})
export class HomeComponent {

  public produtos: any = [
    {
      nome: 'Sanduiche',
      preço: '14,99',
      categoria: 'Sanduíche',
      descrição: 'bão dms'
    },
    {
      nome: 'Beirute',
      preço: '14,99',
      categoria: 'Sanduíche',
      descrição: 'bão dms'
    },
    {
      nome: 'Pizza',
      preço: '14,99',
      categoria: 'Pizza',
      descrição: 'bão dms'
    },
    {
      nome: 'Coca-cola',
      preço: '14,99',
      categoria: 'Bebidas',
      descrição: 'bão dms'
    }
  ]


}
<header>
  <img src="../../assets/burger-fast-food-svgrepo-com.svg" alt="" />
  <h1>E-Menu</h1>
</header>
<main>
  <section>
    <accordion *ngFor="let produto of produtos">
        <accordion-group [heading]="produto.categoria"></accordion-group>
    </accordion>
  </section>
</main>
