import { Component } from '@angular/core';
import 

@Component({
  selector: 'app-menu',
  templateUrl: './menu.component.html',
  styleUrls: ['./menu.component.scss']
})
export class MenuComponent {

}
<accordion *ngFor="let categoria of categorias" [isAnimated]="true">
  <accordion-group [heading]="categoria">
    <div *ngFor="let produto of produtos" class="categories-organizer">
      <div *ngIf="produto.categoria === categoria">
        <p>{{ produto.nome }}</p>
        <p>{{produto.preco}}</p>
      </div>
    </div>
  </accordion-group>
</accordion>
